import React, { useState } from 'react';
import { motion, useMotionValue, useTransform } from 'framer-motion';
import './gymbro.css';

function Gymbro() {
    const [friends, setFriends] = useState([]);
    const [recommendations, setRecommendations] = useState([
        { id: 1, name: "Alex", bio: "Gym lover and fitness freak", img: "/public/alex.jpg" },
        { id: 2, name: "Jordan", bio: "Powerlifting enthusiast", img: "/public/jordan.jpg" },
        { id: 3, name: "Taylor", bio: "Yoga and wellness advocate", img: "/public/taylor.jpg" },
    ]);

    const handleSwipe = (user, direction) => {
        if (direction === "right") {
            setFriends([...friends, user]);
        }
        setRecommendations((prev) => prev.filter((u) => u.id !== user.id));
    };

    return (
        <div className="gymbroContainer">
            <div className="sidebar">
                <h3>Gymbros</h3>
                <ul>
                    {friends.map((friend, index) => (
                        <li key={index}>
                            <img src={friend.img} alt={`${friend.name}'s profile`} className="profileImage" />
                            {friend.name}
                        </li>
                    ))}
                </ul>
            </div>

            <div className="gymbroBody">
                <div className="cardStack">
                    {recommendations.slice(0, 3).map((user, index) => (
                        <motion.div
                            key={user.id}
                            className="card"
                            drag={index === 0 ? "x" : false}  // Only allow dragging for the top card
                            style={{
                                zIndex: recommendations.length - index,
                                x: index === 0 ? useMotionValue(0) : undefined,
                                background: useTransform(
                                    useMotionValue(0),
                                    [-100, 0, 100],
                                    ["#ff0000", "#7700ff", "#008000"]
                                )
                            }}
                            dragConstraints={{ left: 0, right: 0 }}
                            onDragEnd={(e, info) => {
                                if (index === 0) {
                                    if (info.offset.x > 100) handleSwipe(user, "right");
                                    else if (info.offset.x < -100) handleSwipe(user, "left");
                                }
                            }}
                        >
                            <div className="cardImage" style={{ backgroundImage: `url(${user.img})` }}></div>
                            <div className="cardInfo">
                                <h2>{user.name}</h2>
                                <p>{user.bio}</p>
                            </div>
                        </motion.div>
                    ))}
                </div>
            </div>
        </div>
    );
}

export default Gymbro;
