import React, { useState } from 'react';
import { motion, useMotionValue, useTransform } from 'framer-motion';
import './gymbro.css';

function Gymbro() {
    const [friends, setFriends] = useState([]);
    const [recommendations, setRecommendations] = useState([
        { id: 1, name: "Alex", bio: "Gym lover and fitness freak", img: "/public/alex.jpg" },
        { id: 2, name: "Jordan", bio: "Powerlifting enthusiast", img: "/public/jordan.jpg" },
        { id: 3, name: "Taylor", bio: "Yoga and wellness advocate", img: "/public/taylor.jpg" },
    ]);
    const x = useMotionValue(0);
    const background = useTransform(x, [-100, 0, 100], ["#ff0000", "#7700ff", "#008000"]);

    const handleSwipe = (user, direction) => {
        if (direction === "right") {
            setFriends([...friends, user]);
        }
        setRecommendations(recommendations.filter((u) => u.id !== user.id));
        x.set(0); // Reset position after swipe
    };

    return (
        <div className="gymbroContainer">
            <div className="sidebar">
                <h3>Gymbros</h3>
                <ul>
                    {/* Example Friend */}
                    <li><img className="profileImage" src="/path/to/image" alt="Friend" /> Friend Name</li>
                    {/* Repeat for other friends */}
                </ul>
            </div>
            <div className="gymbroBody">
                <div className="cardStack">
                    <div className="card">
                        <div className="cardImage" style={{ backgroundImage: "url('/path/to/user-image1.jpg')" }}></div>
                        <div className="cardInfo">
                            <h2>User 1</h2>
                            <p>User 1 Bio</p>
                        </div>
                    </div>
                    <div className="card">
                        <div className="cardImage" style={{ backgroundImage: "url('/path/to/user-image2.jpg')" }}></div>
                        <div className="cardInfo">
                            <h2>User 2</h2>
                            <p>User 2 Bio</p>
                        </div>
                    </div>
                    <div className="card">
                        <div className="cardImage" style={{ backgroundImage: "url('/path/to/user-image3.jpg')" }}></div>
                        <div className="cardInfo">
                            <h2>User 3</h2>
                            <p>User 3 Bio</p>
                        </div>
                    </div>
                </div>
            </div>
        </div>

    );
}

export default Gymbro;
