import React, { useState, useEffect } from 'react';
import axios from 'axios';

const MakeYourRoutine = () => {
  const [day, setDay] = useState('');
  const [muscle, setMuscle] = useState('');
  const [exercise, setExercise] = useState('');
  const [exercises, setExercises] = useState([]);
  const [user, setUser] = useState(null);

  useEffect(() => {
    const loggedInUser = JSON.parse(localStorage.getItem('user'));
    setUser(loggedInUser);
    fetchExercises(loggedInUser.uuid);
  }, []);

  const fetchExercises = async (userId) => {
    try {
      const response = await axios.get(`/api/users/${userId}/exercises`);
      setExercises(response.data);
    } catch (error) {
      console.error('Fetch exercises error:', error);
    }
  };

  const handleAddExercise = async () => {
    try {
      const response = await axios.post(`/api/users/${user.uuid}/exercises`, {
        day,
        muscle,
        name: exercise,
      });
      setExercises(response.data);
      setDay('');
      setMuscle('');
      setExercise('');
    } catch (error) {
      console.error('Add exercise error:', error);
    }
  };

  return (
    <div>
      <h1>Make Your Routine</h1>
      <form onSubmit={handleAddExercise}>
        <label>
          Choose your day:
          <input type="text" value={day} onChange={(e) => setDay(e.target.value)} />
        </label>
        <label>
          Choose which muscle:
          <input type="text" value={muscle} onChange={(e) => setMuscle(e.target.value)} />
        </label>
        <label>
          Name of the Exercise:
          <input type="text" value={exercise} onChange={(e) => setExercise(e.target.value)} />
        </label>
        <button type="submit">Add Exercise</button>
      </form>
      <h2>Your Exercises</h2>
      <ul>
        {exercises.map((exercise, index) => (
          <li key={index}>
            Day: {exercise.day}, Muscle: {exercise.muscle}, Name: {exercise.name}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default MakeYourRoutine;
